    fun generate(): MutableList<Question> {
        val result = mutableListOf<Question>()
        val resultNum = (0 until list.count()).toMutableList().apply { shuffle() }
        for (index in 0 until 15) {
            val right = Random.nextInt(4)
            result.add(
                Question(
                    list[resultNum[index]].question,
                    if (right == 0) list[resultNum[index]].answer else list[Random.nextInt(list.count())].answer,
                    if (right == 1) list[resultNum[index]].answer else list[Random.nextInt(list.count())].answer,
                    if (right == 2) list[resultNum[index]].answer else list[Random.nextInt(list.count())].answer,
                    if (right == 3) list[resultNum[index]].answer else list[Random.nextInt(list.count())].answer,
                    right + 1,
                )
            )
        }
        return result
    }

    fun randomQuestion() = list[Random.nextInt(list.count())]
